// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SimplePayment
 * @dev Simple one-to-one payment contract optimized for Hedera
 */
contract SimplePayment is ReentrancyGuard, Ownable {

    error InsufficientPayment();
    error TransferFailed();
    error InvalidRecipient();

    event PaymentSent(
        address indexed sender,
        address indexed recipient,
        uint256 amount,
        uint256 timestamp
    );

    event PaymentReceived(
        address indexed from,
        uint256 amount
    );

    // Minimum payment amount (1 tinybar in wei for Hedera)
    uint256 public constant MIN_PAYMENT = 10000000000; // 1 tinybar = 10^10 wei

    // Track total payments
    uint256 public totalPayments;
    uint256 public totalVolume;

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Send payment to a single recipient
     * @param recipient Address to send payment to
     */
    function sendPayment(address recipient) external payable nonReentrant {
        if (recipient == address(0)) revert InvalidRecipient();
        if (msg.value < MIN_PAYMENT) revert InsufficientPayment();

        // Send the payment
        (bool success, ) = recipient.call{value: msg.value}("");
        if (!success) revert TransferFailed();

        // Update tracking
        totalPayments++;
        totalVolume += msg.value;

        emit PaymentSent(msg.sender, recipient, msg.value, block.timestamp);
    }

    /**
     * @notice Send specific amount to recipient (with exact value check)
     * @param recipient Address to send payment to
     * @param amount Exact amount to send
     */
    function sendExactPayment(address recipient, uint256 amount) external payable nonReentrant {
        if (recipient == address(0)) revert InvalidRecipient();
        if (amount < MIN_PAYMENT) revert InsufficientPayment();
        if (msg.value != amount) revert InsufficientPayment();

        // Send the exact payment
        (bool success, ) = recipient.call{value: amount}("");
        if (!success) revert TransferFailed();

        // Update tracking
        totalPayments++;
        totalVolume += amount;

        emit PaymentSent(msg.sender, recipient, amount, block.timestamp);
    }

    /**
     * @notice Get contract balance
     */
    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @notice Get payment statistics
     */
    function getStats() external view returns (uint256 payments, uint256 volume) {
        return (totalPayments, totalVolume);
    }

    /**
     * @notice Emergency withdraw (owner only)
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            (bool success, ) = owner().call{value: balance}("");
            if (!success) revert TransferFailed();
        }
    }

    /**
     * @notice Receive function to accept direct payments
     */
    receive() external payable {
        emit PaymentReceived(msg.sender, msg.value);
    }

    /**
     * @notice Fallback function
     */
    fallback() external payable {
        emit PaymentReceived(msg.sender, msg.value);
    }
}

